DROP TABLE IF EXISTS products;
CREATE TABLE products (
  product_id INTEGER PRIMARY KEY,
  name TEXT NOT NULL,
  sku TEXT UNIQUE,
  price DOUBLE CHECK (price >= 0),
  stock_quantity INTEGER DEFAULT 0
);
INSERT INTO products VALUES (1,'Widget','W-100',10.0,100),(2,'Gadget','G-200',25.0,50);
SELECT * FROM products;

DROP TABLE IF EXISTS cs_people;
CREATE TABLE cs_people (id INTEGER PRIMARY KEY, name TEXT NOT NULL DEFAULT 'Unknown');
INSERT INTO cs_people(id) VALUES (1);
SELECT * FROM cs_people ORDER BY id;
DROP TABLE IF EXISTS cs_people;

DROP TABLE IF EXISTS cs_users;
CREATE TABLE cs_users (user_id INTEGER PRIMARY KEY, email TEXT UNIQUE);
INSERT INTO cs_users VALUES (1,'a@example.com'),(2,'b@example.com');
SELECT * FROM cs_users ORDER BY user_id;
DROP TABLE IF EXISTS cs_users;

DROP TABLE IF EXISTS cs_in;
DROP TABLE IF EXISTS cs_out;
CREATE TABLE cs_in(id INTEGER, qty INTEGER);
INSERT INTO cs_in VALUES (1,5),(2,-1);
CREATE TABLE cs_out AS SELECT * FROM cs_in WHERE qty >= 0;
SELECT * FROM cs_out ORDER BY id;
DROP TABLE IF EXISTS cs_out; DROP TABLE IF EXISTS cs_in;

DROP TABLE IF EXISTS cs_parent;
DROP TABLE IF EXISTS cs_child;
CREATE TABLE cs_parent(pid INTEGER PRIMARY KEY);
CREATE TABLE cs_child(cid INTEGER, pid INTEGER);
INSERT INTO cs_parent VALUES (1);
INSERT INTO cs_child VALUES (10,1),(11,2);
SELECT c.cid, c.pid, CASE WHEN p.pid IS NULL THEN 'ORPHAN' ELSE 'OK' END AS status
FROM cs_child c LEFT JOIN cs_parent p ON c.pid = p.pid ORDER BY c.cid;
DROP TABLE IF EXISTS cs_child; DROP TABLE IF EXISTS cs_parent;

-- Add a NOT NULL (or stricter rule) by creating revised table then swapping
DROP TABLE IF EXISTS products_new;
CREATE TABLE products_new AS SELECT * FROM products;
-- (Optionally validate / transform here)
DROP TABLE IF EXISTS products;
ALTER TABLE products_new RENAME TO products;
SELECT COUNT(*) AS row_count_after_swap FROM products;

ALTER TABLE products ADD COLUMN discount DOUBLE DEFAULT 0;
CREATE TABLE products_tmp AS SELECT product_id, name, sku, price, stock_quantity, discount FROM products WHERE discount >= 0;
DROP TABLE IF EXISTS products;
ALTER TABLE products_tmp RENAME TO products;
SELECT MIN(discount) AS min_discount_ok FROM products;

DROP TABLE IF EXISTS customer_profiles;
CREATE TABLE customer_profiles (
  profile_id INTEGER PRIMARY KEY,
  customer_id INTEGER NOT NULL,
  first_name TEXT NOT NULL,
  last_name TEXT NOT NULL,
  country TEXT NOT NULL DEFAULT 'US',
  created_ts TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);
INSERT INTO customer_profiles VALUES (1,101,'Alice','Smith','US',CURRENT_TIMESTAMP),(2,102,'Bob','Lee','CA',CURRENT_TIMESTAMP);
SELECT * FROM customer_profiles ORDER BY profile_id;

DROP TABLE IF EXISTS pk_single_col;
DROP TABLE IF EXISTS pk_named;
DROP TABLE IF EXISTS pk_composite;
CREATE TABLE pk_single_col (id INTEGER PRIMARY KEY, name TEXT);
CREATE TABLE pk_named (id INTEGER, name TEXT, CONSTRAINT pk_named_id PRIMARY KEY(id));
CREATE TABLE pk_composite (order_id INTEGER, product_id INTEGER, line_no INTEGER, PRIMARY KEY(order_id, product_id, line_no));
INSERT INTO pk_single_col VALUES (1,'A'),(2,'B');
INSERT INTO pk_named VALUES (1,'X'),(2,'Y');
INSERT INTO pk_composite VALUES (10,100,1),(10,101,1);
SELECT 'single' AS kind, * FROM pk_single_col UNION ALL SELECT 'named', * FROM pk_named;

DROP TABLE IF EXISTS fk_items; DROP TABLE IF EXISTS fk_categories;
CREATE TABLE fk_categories (category_id INTEGER PRIMARY KEY, name TEXT);
INSERT INTO fk_categories VALUES (1,'Hardware'),(2,'Software');
CREATE TABLE fk_items (
  item_id INTEGER PRIMARY KEY,
  category_id INTEGER,
  name TEXT
);
INSERT INTO fk_items VALUES (10,1,'Hammer'),(11,2,'Editor'),(12,99,'Ghost Item');
-- Detect orphan child rows (those lacking a matching parent)
SELECT i.item_id, i.category_id, i.name, CASE WHEN c.category_id IS NULL THEN 'ORPHAN' ELSE 'OK' END AS fk_status
FROM fk_items i LEFT JOIN fk_categories c USING(category_id)
ORDER BY i.item_id;

DROP TABLE IF EXISTS unique_demo;
DROP TABLE IF EXISTS unique_composite;
CREATE TABLE unique_demo (username TEXT UNIQUE, email TEXT UNIQUE, phone TEXT);
CREATE TABLE unique_composite (user_id INTEGER, platform TEXT, handle TEXT, UNIQUE(user_id, platform));
INSERT INTO unique_demo VALUES ('alice','alice@example.com','555-1'),('bob','bob@example.com',NULL);
INSERT INTO unique_composite VALUES (1,'web','alice_web'),(1,'mobile','alice_mob');
SELECT 'single' AS kind, * FROM unique_demo;

DROP TABLE IF EXISTS order_line;
CREATE TABLE order_line (
  order_id INTEGER,
  item_id INTEGER,
  qty INTEGER CHECK (qty > 0),
  unit_price DOUBLE CHECK (unit_price >= 0),
  discount DOUBLE DEFAULT 0 CHECK (discount BETWEEN 0 AND 100),
  line_total DOUBLE
);
INSERT INTO order_line VALUES (1,1,2,10.0,0,20.0),(1,2,1,15.0,10,13.5);
SELECT * FROM order_line;
