examples.sql:
DROP TABLE IF EXISTS authors;
CREATE TABLE authors (author_id INTEGER PRIMARY KEY, name TEXT);
INSERT INTO authors VALUES (1,'Ana'),(2,'Ben');
SELECT * FROM authors ORDER BY author_id;

DROP TABLE IF EXISTS cs_snapshot;
CREATE OR REPLACE TABLE cs_snapshot AS SELECT 1 AS id, 'ok' AS status;
SELECT COUNT(*) AS n FROM cs_snapshot;

DROP TABLE IF EXISTS cs_books;
CREATE TABLE cs_books (book_id INTEGER, title TEXT);
ALTER TABLE cs_books ADD COLUMN in_stock BOOLEAN DEFAULT TRUE;
SELECT COUNT(*) AS has_col FROM pragma_table_info('cs_books') WHERE name='in_stock';

DROP TABLE IF EXISTS cs_src;
CREATE TABLE cs_src (id INTEGER, v TEXT, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
DROP TABLE IF EXISTS cs_like;
CREATE TABLE cs_like AS SELECT * FROM cs_src WHERE 1=0;
SELECT COUNT(*) AS rows FROM cs_like;

DROP TABLE IF EXISTS books;
CREATE TABLE books (book_id INTEGER PRIMARY KEY, title TEXT NOT NULL, author_id INTEGER, price DOUBLE CHECK (price >= 0));
INSERT INTO books VALUES (1,'Book A',1,9.99),(2,'Book B',2,14.99);
SELECT * FROM books ORDER BY book_id;

DROP TABLE IF EXISTS publishers;
CREATE TABLE publishers (publisher_id INTEGER PRIMARY KEY, name TEXT, country TEXT);
INSERT INTO publishers VALUES (1,'HarborPress','US'),(2,'MapleBooks','CA');
SELECT * FROM publishers ORDER BY publisher_id;

DROP TABLE IF EXISTS genres;
CREATE TABLE genres (genre_id INTEGER PRIMARY KEY, name TEXT NOT NULL);
INSERT INTO genres VALUES (1,'Fiction'),(2,'Non-fiction');
SELECT * FROM genres;

DROP TABLE IF EXISTS top_publishers;
CREATE TABLE top_publishers AS
SELECT * FROM (VALUES (1,'HarborPress',1000),(2,'MapleBooks',750)) AS t(publisher_id,name,sales);
SELECT * FROM top_publishers;

DROP TABLE IF EXISTS sales_ctas;
CREATE TABLE sales_ctas AS
SELECT CAST(1 AS INTEGER) AS id, CAST('Widget' AS TEXT) AS item, CAST(9.99 AS DECIMAL(10,2)) AS price;
SELECT typeof(id), typeof(item), typeof(price) FROM sales_ctas;

DROP TABLE IF EXISTS maybe_table;
CREATE TABLE IF NOT EXISTS maybe_table (id INTEGER, note TEXT);
INSERT INTO maybe_table VALUES (1,'created'),(2,'exists_ok');
SELECT COUNT(*) AS n FROM maybe_table;

DROP TABLE IF EXISTS top_publishers;
DROP TABLE IF EXISTS publishers;
DROP TABLE IF EXISTS genres;
SELECT 'cleanup_done' AS status;

DROP TABLE IF EXISTS demo_delete;
CREATE TABLE demo_delete (id INTEGER, v TEXT);
INSERT INTO demo_delete VALUES (1,'x'),(2,'y');
DELETE FROM demo_delete;
SELECT COUNT(*) AS rows_after_delete FROM demo_delete;

DROP TABLE IF EXISTS src_demo;
CREATE TABLE src_demo (id INTEGER, name TEXT, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
DROP TABLE IF EXISTS src_demo_copy;
CREATE TABLE src_demo_copy AS SELECT * FROM src_demo WHERE 1=0;
SELECT COUNT(*) AS copied_rows FROM src_demo_copy;

DROP SCHEMA IF EXISTS s_demo CASCADE;
CREATE SCHEMA s_demo;
CREATE TABLE s_demo.metrics (k TEXT, v INTEGER);
INSERT INTO s_demo.metrics VALUES ('x', 1), ('y', 2);
SELECT * FROM s_demo.metrics ORDER BY k;
DROP TABLE IF EXISTS s_demo.metrics;
DROP SCHEMA IF EXISTS s_demo CASCADE;

DROP TABLE IF EXISTS authors;
DROP TABLE IF EXISTS books;
DROP TABLE IF EXISTS publishers;
DROP TABLE IF EXISTS genres;
