DROP TABLE IF EXISTS order_items; DROP TABLE IF EXISTS orders; DROP TABLE IF EXISTS customers;
CREATE TABLE customers (customer_id INTEGER, name TEXT);
CREATE TABLE orders (order_id INTEGER, customer_id INTEGER, amount DOUBLE);
INSERT INTO customers VALUES (1,'Alice'),(2,'Bob');
INSERT INTO orders VALUES (1,1,100),(2,1,50),(3,3,25);

DROP TABLE IF EXISTS people_j; DROP TABLE IF EXISTS visits;
CREATE TABLE people_j (id INTEGER, name TEXT);
CREATE TABLE visits (visit_id INTEGER, person_id INTEGER, page TEXT);
INSERT INTO people_j VALUES (1,'Alice'),(2,'Bob');
INSERT INTO visits VALUES (10,1,'home'),(11,1,'shop'),(12,3,'home');
-- inner join
SELECT p.name, v.page FROM people_j p INNER JOIN visits v ON p.id = v.person_id ORDER BY p.name;
-- left join
SELECT p.name, v.page FROM people_j p LEFT JOIN visits v ON p.id = v.person_id ORDER BY p.name;
-- full join (note extra row for unmatched visit)
SELECT p.name, v.page FROM people_j p FULL JOIN visits v ON p.id = v.person_id ORDER BY p.name NULLS LAST;
DROP TABLE IF EXISTS people_j; DROP TABLE IF EXISTS visits;

DROP TABLE IF EXISTS customers_j; DROP TABLE IF EXISTS orders_j;
CREATE TABLE customers_j (customer_id INTEGER, name TEXT);
CREATE TABLE orders_j (order_id INTEGER, customer_id INTEGER, amount DOUBLE);
INSERT INTO customers_j VALUES (1,'Alice'),(2,'Bob');
INSERT INTO orders_j VALUES (1,1,100),(2,1,50),(3,2,25);
SELECT c.name, o.order_id, o.amount FROM customers_j c INNER JOIN orders_j o ON c.customer_id = o.customer_id ORDER BY c.name;
DROP TABLE IF EXISTS customers_j; DROP TABLE IF EXISTS orders_j;

DROP TABLE IF EXISTS cust_l; DROP TABLE IF EXISTS ord_l;
CREATE TABLE cust_l (id INTEGER, name TEXT);
CREATE TABLE ord_l (id INTEGER, cust_id INTEGER, amt DOUBLE);
INSERT INTO cust_l VALUES (1,'Alice'),(2,'Bob');
INSERT INTO ord_l VALUES (10,1,100);
SELECT c.name, o.amt FROM cust_l c LEFT JOIN ord_l o ON c.id = o.cust_id;
-- filter safely to include unmatched left rows
SELECT c.name, COALESCE(o.amt,0) AS amt FROM cust_l c LEFT JOIN ord_l o ON c.id = o.cust_id;
DROP TABLE IF EXISTS cust_l; DROP TABLE IF EXISTS ord_l;

DROP TABLE IF EXISTS a_r; DROP TABLE IF EXISTS b_r;
CREATE TABLE a_r (id INTEGER, v TEXT);
CREATE TABLE b_r (id INTEGER, v TEXT);
INSERT INTO a_r VALUES (1,'a');
INSERT INTO b_r VALUES (2,'b');
SELECT a_r.v AS a, b_r.v AS b FROM a_r RIGHT JOIN b_r ON a_r.id = b_r.id;
-- equivalent left join by swapping
SELECT b_r.v AS b, a_r.v AS a FROM b_r LEFT JOIN a_r ON b_r.id = a_r.id;
DROP TABLE IF EXISTS a_r; DROP TABLE IF EXISTS b_r;

DROP TABLE IF EXISTS left_f; DROP TABLE IF EXISTS right_f;
CREATE TABLE left_f (id INTEGER, a TEXT);
CREATE TABLE right_f (id INTEGER, b TEXT);
INSERT INTO left_f VALUES (1,'L1');
INSERT INTO right_f VALUES (2,'R2');
SELECT l.id AS l_id, r.id AS r_id, l.a, r.b FROM left_f l FULL JOIN right_f r ON l.id = r.id;
DROP TABLE IF EXISTS left_f; DROP TABLE IF EXISTS right_f;

DROP TABLE IF EXISTS prod_s;
CREATE TABLE prod_s (id INTEGER, name TEXT, category TEXT);
INSERT INTO prod_s VALUES (1,'A','X'),(2,'B','X'),(3,'C','Y');
SELECT p1.name AS a, p2.name AS b FROM prod_s p1 JOIN prod_s p2 ON p1.category = p2.category AND p1.id < p2.id;
DROP TABLE IF EXISTS prod_s;

DROP TABLE IF EXISTS inventory;
CREATE TABLE inventory (sku TEXT, qty INTEGER);
INSERT INTO inventory VALUES ('s1',10);
-- staging values
WITH staging(sku,qty) AS (VALUES ('s1',5),('s2',7))
-- try insert new rows that don't exist yet
INSERT INTO inventory (sku,qty) SELECT s.sku,s.qty FROM staging s LEFT JOIN inventory i ON i.sku = s.sku WHERE i.sku IS NULL;
-- update rows that already exist by joining staging to inventory
UPDATE inventory SET qty = inventory.qty + s.qty FROM (SELECT * FROM (VALUES ('s1',5),('s2',7)) AS s(sku,qty)) s WHERE inventory.sku = s.sku;
SELECT * FROM inventory ORDER BY sku;
DROP TABLE IF EXISTS inventory;

DROP TABLE IF EXISTS j_emps;
CREATE TEMP TABLE j_emps(emp_id INTEGER, name TEXT, manager_id INTEGER);
INSERT INTO j_emps VALUES (1,'CEO',NULL),(2,'Mgr',1),(3,'Dev',2);
SELECT e.emp_id, e.name, m.name AS manager
FROM j_emps e LEFT JOIN j_emps m ON e.manager_id = m.emp_id
ORDER BY e.emp_id;
DROP TABLE IF EXISTS j_emps;
